function process_nsefuture(count,n,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn)
{
    try{
        var mon=count[n].split('-')[0];
        var m=count[n].split('-')[1];
        var d=count[n].split('-')[2];
        var yy=count[n].split('-')[3];
        var y=yy.substr(2,2);
        //import files from modules
        var fs = require('fs');
        var csv = require('ya-csv');
        var async = require('async');
        //var path = require('./node_modules/path');

        //var writeFileStream = fs.createWriteStream('./tempData/output.txt',{flag:'a',encoding:'utf-8'});

    

        async.parallel({
            data1 : function(callback){
                if (fs.existsSync(unzippath+'unzip1/fo'+d+m+yy+'.csv')) {
                    Ext.getCmp('pbar3').updateText('Processing fo'+d+m+yy+'.csv');
                    fs.readFile(unzippath+'unzip1/fo'+d+m+yy+'.csv','utf-8', function(err, data){
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while reading "+unzippath+"unzip1/fo"+d+m+yy+".csv";
                        }
                        //console.log('\n Reading fo.csv');
                        callback(err, data);
                    });
                }else
                {
                    // Here readdir's functionaliity has no use. It is written for only to send
                    // '0' data in its callback function because we cannot call "callback(err, data);"
                    // function out side a callback function
                    fs.readdir(downloadpath, function(err, files){
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while reading "+unzippath+"unzip1/fo"+d+m+yy+".csv";
                        }

                        callback(err,'0');
                    });
                }
            },

            data2 : function(callback){
                if (fs.existsSync(downloadpath+'sec_list.csv')) {
                    fs.readFile(downloadpath+'sec_list.csv','utf-8', function(err, data){
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while reading "+downloadpath+"sec_list.csv.";
                        }
                        // console.log('\n Reading sec_list.csv');
                        callback(err, data);
                    });
                }else
                {
                    // Here readdir's functionaliity has no use. It is written for only to send
                    // '0' data in its callback function because we cannot call "callback(err, data);"
                    // function out side a callback function
                    fs.readdir(downloadpath, function(err, files){
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while reading "+downloadpath+"sec_list.csv.";
                        }
                        callback(err,'0');
                    });
                }

            }
        }, function(err, result){
            if(err)
            {
                document.getElementById("om").innerHTML+="<br/>An error occured while processing "+unzippath+"unzip1/fo"+d+m+yy+".csv";
            }

            //        console.log(unzippath+'unzip1/fo'+d+m+yy+'.csv');
            if (fs.existsSync(unzippath+'unzip1/fo'+d+m+yy+'.csv')) {
                Ext.getCmp('pbar3').updateText('Processing fo'+d+m+yy+'.csv');
                var arrRow = new Array();
                arrRow = result['data1'].split('\n');
            
                var arrFileData = new Array();

                // arrRow.lenght-3 is for last 3 lines of no use in file
                for(var i=0 ; i<arrRow.length-3;i++)
                {
                    var tempArr = arrRow[i].split(',');
                    arrFileData.push(tempArr);
                }

                // Read Sec_List
                if (fs.existsSync(downloadpath+'sec_list.csv')) {
                    var arrRow1 = new Array();
                    arrRow1 = result['data2'].split('\n');

                    var arrFileData1 = new Array();

                    for(i=0 ; i<arrRow1.length-1 ;i++)
                    {
                        var tempArr1 = arrRow1[i].split(',');
                        arrFileData1.push(tempArr1);
                    }

                    for(i=1; i<arrFileData.length; i++)
                    {
                        for(var j=1; j<arrFileData1.length; j++)
                        {
                            if(arrFileData[i][1].toString().trim()==arrFileData1[j][0].toString().trim())
                            {
                                arrFileData[i][12] = arrFileData1[j][2].toString().trim();
                            }
                        }
                    }

                }

                // add blank space where cell of company name col not have value
                for(i=0; i<arrFileData.length; i++)
                {
                    if(arrFileData[i][12] == null || arrFileData[i][12] == "")
                    {
                        arrFileData[i][12] = '';
                    }
                }


                var filename = outputpath+'NSE_Equity_Futures_fo'+d+m+yy+'.csv';
                var header='TICKER,NAME,DATE,OPEN,HIGH,LOW,CLOSE,VOLUME,OPENINT';
                if(!fs.existsSync(filename)){
                    fs.writeFile(filename, header+'\n', function (err)
                    {
                        if(err)
                        {
                            document.getElementById("om").innerHTML+="<br/>An error occured while processing "+unzippath+"unzip1/fo"+d+m+yy+".csv";
                        }

                        //console.log(err);
                        // console.log(filename);
                        //processing actual file data
                        var dates = new Array();
                        for(i=0;i<arrFileData.length;i++)
                        {
                            if(typeof arrFileData[i][2]!=="undefined" && arrFileData[i][2]!=="" && arrFileData[i][2]!==null && arrFileData[i][2]!= "\"N/A\"" && arrFileData[i][2]!= "N/A" && arrFileData[i][2]!= "NaN" && arrFileData[i][2]!= "undefined")if(typeof arrFileData[i][2]!=="undefined" && arrFileData[i][2]!=="" && arrFileData[i][2]!==null && arrFileData[i][2]!= "\"N/A\"" && arrFileData[i][2]!= "N/A" && arrFileData[i][2]!= "NaN" && arrFileData[i][2]!= "undefined")
                                dates.push(new Date(arrFileData[i][2].split('/')[2],arrFileData[i][2].split('/')[1]-1,arrFileData[i][2].split('/')[0]));
                        }

                        function sortDates(a, b)
                        {
                            return a.getTime() - b.getTime();
                        }

                        var sorted = dates.sort(sortDates);
                        var m1=sorted[1].getMonth()+1;
                        var day=sorted[1].getFullYear()+'/'+m1+'/'+sorted[1].getDate();
                        var date = yy+m+d;//'20121219';
                        // console.log(date);
                        // Writing data to output file

                        for(i=1;i<arrFileData.length;i++)
                        {
                            var date1 = new Date(day);

                            var date2 = new Date(arrFileData[i][2].split('/')[2]+'/'+arrFileData[i][2].split('/')[1]+'/'+arrFileData[i][2].split('/')[0]);
                            m1=monthDiff(date1,date2);
                            if(m1==0)
                            {
                                fs.appendFileSync(filename,arrFileData[i][1].split(" ")[0]+'-I'+','+arrFileData[i][12]+','+date+','+arrFileData[i][3]+','+arrFileData[i][4]+','+arrFileData[i][5]+','+arrFileData[i][6]+','+arrFileData[i][9].toString().trim()+','+arrFileData[i][7]+'\n','UTF-8', function (err) {
                                    if(err)
                                    {
                                        document.getElementById("om").innerHTML+="<br/>An error occured while processing "+unzippath+"unzip1/fo"+d+m+yy+".csv";
                                    }

                                // console.log('| Appended to file!');
                                });
                            }
                            if(m1==1)
                            {
                                fs.appendFileSync(filename,arrFileData[i][1].split(" ")[0]+'-II'+','+arrFileData[i][12]+','+date+','+arrFileData[i][3]+','+arrFileData[i][4]+','+arrFileData[i][5]+','+arrFileData[i][6]+','+arrFileData[i][9].toString().trim()+','+arrFileData[i][7]+'\n','UTF-8', function (err) {
                                    if(err)
                                    {
                                        document.getElementById("om").innerHTML+="<br/>An error occured while processing "+unzippath+"unzip1/fo"+d+m+yy+".csv";
                                    }

                                // console.log('| Appended to file!');
                                });
                            }
                            if(m1==2)
                            {
                                fs.appendFileSync(filename,arrFileData[i][1].split(" ")[0]+'-III'+','+arrFileData[i][12]+','+date+','+arrFileData[i][3]+','+arrFileData[i][4]+','+arrFileData[i][5]+','+arrFileData[i][6]+','+arrFileData[i][9].toString().trim()+','+arrFileData[i][7]+'\n','UTF-8', function (err) {
                                    if(err)
                                    {
                                        document.getElementById("om").innerHTML+="<br/>An error occured while processing "+unzippath+"unzip1/fo"+d+m+yy+".csv";
                                    }


                                });


                            }

                        }// for
               


                    });
                }

          
            }
        });

	
        //Ext.getCmp('pbar3').updateText('fo'+d+m+yy+'.csv is processed...');
        document.getElementById("om").innerHTML+="<br/>fo"+d+mon+yy+".csv is processed..";
        addlog(logpath, 'fo'+d+mon+yy+'.csv is processed..');
        n++;
        if(count.length>n)
        {
            loadprogressbar();
            setTimeout(function (){
                process_nsefuture(count,n,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn)
                }, 3000);
        //document.getElementById("om").innerHTML+='..test';
        }
        else
        {
            Ext.getCmp('pbar3').updateText('');
            Ext.getCmp('pbar3').updateProgress(0);
            /************/
            visit('nsef.html');
            /***************/
            sn++;
            callfun(count,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn);
        }

    }
    catch(err)
    {

        document.getElementById("om").innerHTML+="<br/> An error occured while processing fo"+d+mon+yy+".csv";
        n++;
        if(count.length>n)
        {
            loadprogressbar();
            setTimeout(function (){
                process_nsefuture(count,n,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn)
                }, 3000);
	
        }
        else
        {
            Ext.getCmp('pbar3').updateText('');
            Ext.getCmp('pbar3').updateProgress(0);
            /************/
            visit('nsef.html');
            /***************/
            sn++;
            callfun(count,baseurl,downloadpath,unzippath,outputpath,logpath,fromd,tod,sn);
        }

    }
    }


function monthDiff(d1, d2) {
    try
    {
        var months;
        months = (d2.getFullYear() - d1.getFullYear()) * 12;
        months -= d1.getMonth() + 1;
        months += d2.getMonth();
        return months+1;
    }catch(err)
    {
        console.log(err);
    }
}







    



